import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { 
  LightBulbIcon, 
  ClockIcon, 
  CalendarIcon,
  CogIcon,
  SunIcon,
  MoonIcon,
  AdjustmentsHorizontalIcon,
  SparklesIcon
} from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';

const LightControl = () => {
  const [lights, setLights] = useState({
    living_room: { status: 'off', brightness: 0, color_temperature: 'warm' },
    kitchen: { status: 'off', brightness: 0, color_temperature: 'warm' },
    bedroom: { status: 'off', brightness: 0, color_temperature: 'warm' },
    bathroom: { status: 'off', brightness: 0, color_temperature: 'warm' },
    office: { status: 'off', brightness: 0, color_temperature: 'warm' }
  });

  const [schedules, setSchedules] = useState({});
  const [selectedRoom, setSelectedRoom] = useState('living_room');
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchLightStatus();
  }, []);

  const fetchLightStatus = async () => {
    try {
      const response = await fetch('/api/status');
      const data = await response.json();
      setLights(data.lights || {});
    } catch (error) {
      console.error('Error fetching light status:', error);
    }
  };

  const controlLight = async (room, action, brightness = 100) => {
    try {
      setIsLoading(true);
      const response = await fetch(`/api/lights/${room}/control`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ action, brightness }),
      });

      if (response.ok) {
        const result = await response.json();
        setLights(prev => ({
          ...prev,
          [room]: result.state
        }));
        toast.success(`${action} ${room} lights`);
      }
    } catch (error) {
      toast.error('Error controlling lights');
    } finally {
      setIsLoading(false);
    }
  };

  const toggleLight = async (room) => {
    try {
      setIsLoading(true);
      const response = await fetch(`/api/lights/${room}/toggle`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const result = await response.json();
        setLights(prev => ({
          ...prev,
          [room]: { ...prev[room], status: result.status }
        }));
        toast.success(`${result.status} ${room} lights`);
      }
    } catch (error) {
      toast.error('Error toggling lights');
    } finally {
      setIsLoading(false);
    }
  };

  const setBrightness = async (room, brightness) => {
    try {
      const response = await fetch(`/api/lights/${room}/brightness`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ brightness }),
      });

      if (response.ok) {
        const result = await response.json();
        setLights(prev => ({
          ...prev,
          [room]: { ...prev[room], brightness: result.brightness }
        }));
      }
    } catch (error) {
      toast.error('Error adjusting brightness');
    }
  };

  const setColorTemperature = async (room, temperature) => {
    try {
      const response = await fetch(`/api/lights/${room}/color`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ temperature }),
      });

      if (response.ok) {
        const result = await response.json();
        setLights(prev => ({
          ...prev,
          [room]: { ...prev[room], color_temperature: result.temperature }
        }));
        toast.success(`Set ${room} to ${temperature} lighting`);
      }
    } catch (error) {
      toast.error('Error setting color temperature');
    }
  };

  const bulkControl = async (action, brightness = 100) => {
    try {
      setIsLoading(true);
      const response = await fetch('/api/lights/bulk', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ action, brightness }),
      });

      if (response.ok) {
        const result = await response.json();
        setLights(prev => ({
          ...prev,
          ...result.results
        }));
        toast.success(`${action} all lights`);
      }
    } catch (error) {
      toast.error('Error controlling all lights');
    } finally {
      setIsLoading(false);
    }
  };

  const getRoomDisplayName = (room) => {
    return room.split('_').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  const getStatusColor = (status) => {
    return status === 'on' ? 'text-green-600' : 'text-gray-400';
  };

  const getBrightnessColor = (brightness) => {
    if (brightness === 0) return 'text-gray-400';
    if (brightness <= 30) return 'text-yellow-400';
    if (brightness <= 70) return 'text-yellow-500';
    return 'text-yellow-600';
  };

  return (
    <div className="p-6">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Light Control</h1>
        <p className="text-gray-600 mt-2">
          Real-time control of your smart lighting system
        </p>
      </div>

      {/* Quick Actions */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white p-6 rounded-lg shadow-md mb-8"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Quick Actions</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => bulkControl('on')}
            disabled={isLoading}
            className="bg-green-600 text-white py-3 px-4 rounded-lg hover:bg-green-700 flex items-center justify-center disabled:opacity-50"
          >
            <SunIcon className="w-5 h-5 mr-2" />
            All On
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => bulkControl('off')}
            disabled={isLoading}
            className="bg-gray-600 text-white py-3 px-4 rounded-lg hover:bg-gray-700 flex items-center justify-center disabled:opacity-50"
          >
            <MoonIcon className="w-5 h-5 mr-2" />
            All Off
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => bulkControl('dim', 50)}
            disabled={isLoading}
            className="bg-yellow-600 text-white py-3 px-4 rounded-lg hover:bg-yellow-700 flex items-center justify-center disabled:opacity-50"
          >
            <LightBulbIcon className="w-5 h-5 mr-2" />
            Dim All
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            disabled={isLoading}
            className="bg-purple-600 text-white py-3 px-4 rounded-lg hover:bg-purple-700 flex items-center justify-center disabled:opacity-50"
          >
            <SparklesIcon className="w-5 h-5 mr-2" />
            AI Mode
          </motion.button>
        </div>
      </motion.div>

      {/* Room Controls */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {Object.entries(lights).map(([room, light]) => (
          <motion.div
            key={room}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white p-6 rounded-lg shadow-md"
          >
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900">
                {getRoomDisplayName(room)}
              </h3>
              <div className={`w-3 h-3 rounded-full ${light.status === 'on' ? 'bg-green-500' : 'bg-gray-300'}`}></div>
            </div>

            {/* Status and Toggle */}
            <div className="flex items-center justify-between mb-4">
              <span className={`text-sm font-medium ${getStatusColor(light.status)}`}>
                {light.status === 'on' ? 'ON' : 'OFF'}
              </span>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => toggleLight(room)}
                disabled={isLoading}
                className={`px-4 py-2 rounded-lg text-sm font-medium ${
                  light.status === 'on' 
                    ? 'bg-red-100 text-red-700 hover:bg-red-200' 
                    : 'bg-green-100 text-green-700 hover:bg-green-200'
                } disabled:opacity-50`}
              >
                {light.status === 'on' ? 'Turn Off' : 'Turn On'}
              </motion.button>
            </div>

            {/* Brightness Control */}
            <div className="mb-4">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm font-medium text-gray-700">Brightness</span>
                <span className={`text-sm font-bold ${getBrightnessColor(light.brightness)}`}>
                  {light.brightness}%
                </span>
              </div>
              <input
                type="range"
                min="0"
                max="100"
                value={light.brightness}
                onChange={(e) => setBrightness(room, parseInt(e.target.value))}
                className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer slider"
                disabled={isLoading}
              />
            </div>

            {/* Color Temperature */}
            <div className="mb-4">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm font-medium text-gray-700">Color Temperature</span>
                <AdjustmentsHorizontalIcon className="w-4 h-4 text-gray-500" />
              </div>
              <div className="flex space-x-2">
                {['warm', 'neutral', 'cool'].map((temp) => (
                  <button
                    key={temp}
                    onClick={() => setColorTemperature(room, temp)}
                    disabled={isLoading}
                    className={`px-3 py-1 rounded text-xs font-medium ${
                      light.color_temperature === temp
                        ? 'bg-blue-100 text-blue-700'
                        : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                    } disabled:opacity-50 capitalize`}
                  >
                    {temp}
                  </button>
                ))}
              </div>
            </div>

            {/* Quick Actions */}
            <div className="flex space-x-2">
              <button
                onClick={() => setBrightness(room, 100)}
                disabled={isLoading}
                className="flex-1 px-3 py-2 bg-blue-100 text-blue-700 rounded text-sm font-medium hover:bg-blue-200 disabled:opacity-50"
              >
                Full
              </button>
              <button
                onClick={() => setBrightness(room, 50)}
                disabled={isLoading}
                className="flex-1 px-3 py-2 bg-yellow-100 text-yellow-700 rounded text-sm font-medium hover:bg-yellow-200 disabled:opacity-50"
              >
                Half
              </button>
              <button
                onClick={() => setBrightness(room, 25)}
                disabled={isLoading}
                className="flex-1 px-3 py-2 bg-orange-100 text-orange-700 rounded text-sm font-medium hover:bg-orange-200 disabled:opacity-50"
              >
                Low
              </button>
            </div>
          </motion.div>
        ))}
      </div>

      {/* AI Features Section */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white p-6 rounded-lg shadow-md mt-8"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Smart Features</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <h3 className="text-lg font-medium text-gray-900 mb-4">Motion Detection</h3>
            <div className="space-y-4">
              <div className="p-4 bg-blue-50 rounded-lg">
                <div className="flex items-center mb-2">
                  <CogIcon className="w-5 h-5 text-blue-600 mr-2" />
                  <span className="font-medium text-blue-900">Auto On/Off</span>
                </div>
                <p className="text-sm text-blue-700">
                  Lights automatically turn on when motion is detected and off after 5 minutes of inactivity.
                </p>
              </div>

              <div className="p-4 bg-green-50 rounded-lg">
                <div className="flex items-center mb-2">
                  <SunIcon className="w-5 h-5 text-green-600 mr-2" />
                  <span className="font-medium text-green-900">Natural Light Integration</span>
                </div>
                <p className="text-sm text-green-700">
                  Brightness automatically adjusts based on available natural light.
                </p>
              </div>
            </div>
          </div>

          <div>
            <h3 className="text-lg font-medium text-gray-900 mb-4">Smart Scheduling</h3>
            <div className="space-y-4">
              <div className="p-4 bg-purple-50 rounded-lg">
                <div className="flex items-center mb-2">
                  <CalendarIcon className="w-5 h-5 text-purple-600 mr-2" />
                  <span className="font-medium text-purple-900">Daily Schedules</span>
                </div>
                <p className="text-sm text-purple-700">
                  Set different lighting patterns for different times of day.
                </p>
              </div>

              <div className="p-4 bg-indigo-50 rounded-lg">
                <div className="flex items-center mb-2">
                  <ClockIcon className="w-5 h-5 text-indigo-600 mr-2" />
                  <span className="font-medium text-indigo-900">Usage Learning</span>
                </div>
                <p className="text-sm text-indigo-700">
                  AI learns your patterns and optimizes lighting schedules accordingly.
                </p>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default LightControl;
