import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { 
  ClockIcon, 
  CalendarIcon,
  SunIcon,
  MoonIcon,
  PlayIcon,
  PauseIcon,
  SparklesIcon,
  ShieldCheckIcon,
  AdjustmentsHorizontalIcon
} from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';

const Scheduling = () => {
  const [schedules, setSchedules] = useState({});
  const [selectedRoom, setSelectedRoom] = useState('living_room');
  const [selectedDay, setSelectedDay] = useState('monday');
  const [isLoading, setIsLoading] = useState(false);

  const days = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];

  useEffect(() => {
    fetchSchedules();
  }, []);

  const fetchSchedules = async () => {
    try {
      const response = await fetch('/api/schedules');
      const data = await response.json();
      setSchedules(data);
    } catch (error) {
      console.error('Error fetching schedules:', error);
    }
  };

  const updateSchedule = async (room, scheduleData) => {
    try {
      setIsLoading(true);
      const response = await fetch(`/api/schedules/${room}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(scheduleData),
      });

      if (response.ok) {
        const result = await response.json();
        setSchedules(prev => ({
          ...prev,
          [room]: result.schedule
        }));
        toast.success('Schedule updated successfully');
      }
    } catch (error) {
      toast.error('Error updating schedule');
    } finally {
      setIsLoading(false);
    }
  };

  const toggleSchedule = async (room, enabled) => {
    try {
      const response = await fetch(`/api/schedules/${room}/enable`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ enabled }),
      });

      if (response.ok) {
        setSchedules(prev => ({
          ...prev,
          [room]: { ...prev[room], enabled }
        }));
        toast.success(`Schedule ${enabled ? 'enabled' : 'disabled'} for ${room}`);
      }
    } catch (error) {
      toast.error('Error toggling schedule');
    }
  };

  const toggleVacationMode = async (room, vacationMode) => {
    try {
      const response = await fetch(`/api/schedules/${room}/vacation`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ vacation_mode: vacationMode }),
      });

      if (response.ok) {
        setSchedules(prev => ({
          ...prev,
          [room]: { ...prev[room], vacation_mode: vacationMode }
        }));
        toast.success(`Vacation mode ${vacationMode ? 'enabled' : 'disabled'} for ${room}`);
      }
    } catch (error) {
      toast.error('Error toggling vacation mode');
    }
  };

  const toggleSunriseSunset = async (room, sunriseSunset) => {
    try {
      const response = await fetch(`/api/schedules/${room}/sunrise-sunset`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ sunrise_sunset: sunriseSunset }),
      });

      if (response.ok) {
        setSchedules(prev => ({
          ...prev,
          [room]: { ...prev[room], sunrise_sunset: sunriseSunset }
        }));
        toast.success(`Sunrise/sunset mode ${sunriseSunset ? 'enabled' : 'disabled'} for ${room}`);
      }
    } catch (error) {
      toast.error('Error toggling sunrise/sunset mode');
    }
  };

  const updateScheduleTimes = async (room, day, times) => {
    try {
      const response = await fetch(`/api/schedules/${room}/times`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ day, times }),
      });

      if (response.ok) {
        setSchedules(prev => ({
          ...prev,
          [room]: {
            ...prev[room],
            daily_schedule: {
              ...prev[room].daily_schedule,
              [day]: times
            }
          }
        }));
        toast.success('Schedule times updated');
      }
    } catch (error) {
      toast.error('Error updating schedule times');
    }
  };

  const getRoomDisplayName = (room) => {
    return room.split('_').map(word => 
      word.charAt(0).toUpperCase() + word.slice(1)
    ).join(' ');
  };

  const getDayDisplayName = (day) => {
    return day.charAt(0).toUpperCase() + day.slice(1);
  };

  const addScheduleEvent = (room, day) => {
    const currentSchedule = schedules[room]?.daily_schedule?.[day] || [];
    const newEvent = {
      time: '12:00',
      action: 'on',
      brightness: 100
    };

    const updatedTimes = [...currentSchedule, newEvent];
    updateScheduleTimes(room, day, updatedTimes);
  };

  const removeScheduleEvent = (room, day, index) => {
    const currentSchedule = schedules[room]?.daily_schedule?.[day] || [];
    const updatedTimes = currentSchedule.filter((_, i) => i !== index);
    updateScheduleTimes(room, day, updatedTimes);
  };

  const updateScheduleEvent = (room, day, index, field, value) => {
    const currentSchedule = schedules[room]?.daily_schedule?.[day] || [];
    const updatedTimes = currentSchedule.map((event, i) => 
      i === index ? { ...event, [field]: value } : event
    );
    updateScheduleTimes(room, day, updatedTimes);
  };

  return (
    <div className="p-6">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Smart Scheduling</h1>
        <p className="text-gray-600 mt-2">
          Set up automated lighting schedules for energy efficiency and convenience
        </p>
      </div>

      {/* Room Selection */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white p-6 rounded-lg shadow-md mb-8"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Select Room</h2>
        <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
          {Object.keys(schedules).map((room) => (
            <button
              key={room}
              onClick={() => setSelectedRoom(room)}
              className={`p-4 rounded-lg border-2 transition-colors ${
                selectedRoom === room
                  ? 'border-blue-500 bg-blue-50 text-blue-700'
                  : 'border-gray-200 hover:border-gray-300'
              }`}
            >
              <div className="text-sm font-medium">{getRoomDisplayName(room)}</div>
              <div className={`text-xs mt-1 ${
                schedules[room]?.enabled ? 'text-green-600' : 'text-gray-500'
              }`}>
                {schedules[room]?.enabled ? 'Active' : 'Inactive'}
              </div>
            </button>
          ))}
        </div>
      </motion.div>

      {selectedRoom && schedules[selectedRoom] && (
        <>
          {/* Schedule Controls */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white p-6 rounded-lg shadow-md mb-8"
          >
            <h2 className="text-xl font-semibold text-gray-900 mb-4">
              {getRoomDisplayName(selectedRoom)} Schedule
            </h2>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {/* Enable/Disable Schedule */}
              <div className="flex items-center justify-between p-4 border border-gray-200 rounded-lg">
                <div className="flex items-center">
                  <PlayIcon className="w-6 h-6 text-green-600 mr-3" />
                  <div>
                    <h3 className="font-medium text-gray-900">Schedule Active</h3>
                    <p className="text-sm text-gray-600">Enable automated scheduling</p>
                  </div>
                </div>
                <label className="relative inline-flex items-center cursor-pointer">
                  <input
                    type="checkbox"
                    checked={schedules[selectedRoom]?.enabled || false}
                    onChange={(e) => toggleSchedule(selectedRoom, e.target.checked)}
                    className="sr-only peer"
                  />
                  <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-blue-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600"></div>
                </label>
              </div>

              {/* Vacation Mode */}
              <div className="flex items-center justify-between p-4 border border-gray-200 rounded-lg">
                <div className="flex items-center">
                  <ShieldCheckIcon className="w-6 h-6 text-purple-600 mr-3" />
                  <div>
                    <h3 className="font-medium text-gray-900">Vacation Mode</h3>
                    <p className="text-sm text-gray-600">Random patterns for security</p>
                  </div>
                </div>
                <label className="relative inline-flex items-center cursor-pointer">
                  <input
                    type="checkbox"
                    checked={schedules[selectedRoom]?.vacation_mode || false}
                    onChange={(e) => toggleVacationMode(selectedRoom, e.target.checked)}
                    className="sr-only peer"
                  />
                  <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-blue-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600"></div>
                </label>
              </div>

              {/* Sunrise/Sunset */}
              <div className="flex items-center justify-between p-4 border border-gray-200 rounded-lg">
                <div className="flex items-center">
                  <SunIcon className="w-6 h-6 text-orange-600 mr-3" />
                  <div>
                    <h3 className="font-medium text-gray-900">Sunrise/Sunset</h3>
                    <p className="text-sm text-gray-600">Natural light integration</p>
                  </div>
                </div>
                <label className="relative inline-flex items-center cursor-pointer">
                  <input
                    type="checkbox"
                    checked={schedules[selectedRoom]?.sunrise_sunset || false}
                    onChange={(e) => toggleSunriseSunset(selectedRoom, e.target.checked)}
                    className="sr-only peer"
                  />
                  <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-blue-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600"></div>
                </label>
              </div>
            </div>
          </motion.div>

          {/* Daily Schedule */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="bg-white p-6 rounded-lg shadow-md"
          >
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-semibold text-gray-900">Daily Schedule</h2>
              <div className="flex space-x-2">
                {days.map((day) => (
                  <button
                    key={day}
                    onClick={() => setSelectedDay(day)}
                    className={`px-3 py-1 rounded text-sm font-medium ${
                      selectedDay === day
                        ? 'bg-blue-100 text-blue-700'
                        : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                    }`}
                  >
                    {getDayDisplayName(day).slice(0, 3)}
                  </button>
                ))}
              </div>
            </div>

            <div className="space-y-4">
              {schedules[selectedRoom]?.daily_schedule?.[selectedDay]?.map((event, index) => (
                <div key={index} className="flex items-center space-x-4 p-4 border border-gray-200 rounded-lg">
                  <input
                    type="time"
                    value={event.time}
                    onChange={(e) => updateScheduleEvent(selectedRoom, selectedDay, index, 'time', e.target.value)}
                    className="border border-gray-300 rounded px-3 py-2"
                  />

                  <select
                    value={event.action}
                    onChange={(e) => updateScheduleEvent(selectedRoom, selectedDay, index, 'action', e.target.value)}
                    className="border border-gray-300 rounded px-3 py-2"
                  >
                    <option value="on">Turn On</option>
                    <option value="off">Turn Off</option>
                  </select>

                  {event.action === 'on' && (
                    <div className="flex items-center space-x-2">
                      <span className="text-sm text-gray-600">Brightness:</span>
                      <input
                        type="range"
                        min="0"
                        max="100"
                        value={event.brightness || 100}
                        onChange={(e) => updateScheduleEvent(selectedRoom, selectedDay, index, 'brightness', parseInt(e.target.value))}
                        className="w-20"
                      />
                      <span className="text-sm text-gray-600 w-8">{event.brightness || 100}%</span>
                    </div>
                  )}

                  <button
                    onClick={() => removeScheduleEvent(selectedRoom, selectedDay, index)}
                    className="text-red-600 hover:text-red-800"
                  >
                    Remove
                  </button>
                </div>
              ))}

              <button
                onClick={() => addScheduleEvent(selectedRoom, selectedDay)}
                className="w-full p-4 border-2 border-dashed border-gray-300 rounded-lg text-gray-600 hover:border-gray-400 hover:text-gray-800 transition-colors"
              >
                + Add Schedule Event
              </button>
            </div>
          </motion.div>
        </>
      )}
    </div>
  );
};

export default Scheduling;
