  ExclamationTriangleIcon
} from '@heroicons/react/24/outline';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import toast from 'react-hot-toast';
import io from 'socket.io-client';

const Dashboard = () => {
  const [systemStatus, setSystemStatus] = useState({
    lights: {},
    energy: {},
    timestamp: new Date().toISOString()
  });
  const [socket, setSocket] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // Initialize socket connection
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);

    newSocket.on('connect', () => {
      setIsConnected(true);
      toast.success('Connected to AI Light Control System');
    });

    newSocket.on('disconnect', () => {
      setIsConnected(false);
      toast.error('Disconnected from system');
    });

    newSocket.on('light_update', (data) => {
      setSystemStatus(prev => ({
        ...prev,
        lights: {
          ...prev.lights,
          [data.room]: data.state
        }
      }));
    });

    newSocket.on('motion_update', (data) => {
      toast.success(`Motion detected in ${data.room}!`);
    });

    newSocket.on('auto_off', (data) => {
      toast.info(`Lights automatically turned off in ${data.room}`);
    });

    // Fetch initial status
    fetchSystemStatus();

    return () => {
      newSocket.close();
    };
  }, []);

  const fetchSystemStatus = async () => {
    try {
      const response = await fetch('/api/status');
      const data = await response.json();
      setSystemStatus(data);
    } catch (error) {
      console.error('Error fetching system status:', error);
    }
  };

  const controlLight = async (room, action, brightness = 100) => {
    try {
      const response = await fetch(`/api/lights/${room}/control`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ action, brightness }),
      });

      if (response.ok) {
        toast.success(`${action} ${room} lights`);
      }
    } catch (error) {
      toast.error('Error controlling lights');
    }
  };

  const controlAllLights = async (action, brightness = 100) => {
    try {
      const response = await fetch('/api/lights/all', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ action, brightness }),
      });

      if (response.ok) {
        toast.success(`${action} all lights`);
      }
    } catch (error) {
      toast.error('Error controlling all lights');
    }
  };

  const getEnergyData = () => {
    const energy = systemStatus.energy || {};
    return [
      { name: 'Used', value: energy.daily_consumption || 0, color: '#ef4444' },
      { name: 'Saved', value: energy.cost_saved || 0, color: '#10b981' }
    ];
  };

  const getRoomStatus = () => {
    const lights = systemStatus.lights || {};
    return Object.entries(lights).map(([room, state]) => ({
      room,
      status: state.status || 'off',
      brightness: state.brightness || 0,
      motion: state.motion_detected || false
    }));
  };

  const getUsageHistory = () => {
    const energy = systemStatus.energy || {};
    const history = energy.usage_history || [];
    return history.slice(-24).map((entry, index) => ({
      time: index,
      consumption: entry.consumption || 0,
      cost: entry.cost || 0
    }));
  };

  return (
    <div className="p-6">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">AI Smart Light Control</h1>
        <p className="text-gray-600 mt-2">
          Intelligent lighting system saving energy and reducing costs
        </p>
        <div className="flex items-center mt-4">
          <div className={`w-3 h-3 rounded-full mr-2 ${isConnected ? 'bg-green-500' : 'bg-red-500'}`}></div>
          <span className="text-sm text-gray-600">
            {isConnected ? 'System Connected' : 'System Disconnected'}
          </span>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => controlAllLights('on')}
          className="bg-blue-600 text-white p-4 rounded-lg flex items-center justify-center hover:bg-blue-700"
        >
          <SunIcon className="w-6 h-6 mr-2" />
          Turn All On
        </motion.button>

        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => controlAllLights('off')}
          className="bg-gray-600 text-white p-4 rounded-lg flex items-center justify-center hover:bg-gray-700"
        >
          <MoonIcon className="w-6 h-6 mr-2" />
          Turn All Off
        </motion.button>

        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => controlAllLights('dim', 50)}
          className="bg-yellow-600 text-white p-4 rounded-lg flex items-center justify-center hover:bg-yellow-700"
        >
          <LightBulbIcon className="w-6 h-6 mr-2" />
          Dim All
        </motion.button>

        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="bg-green-600 text-white p-4 rounded-lg flex items-center justify-center hover:bg-green-700"
        >
          <CogIcon className="w-6 h-6 mr-2" />
          AI Mode
        </motion.button>
      </div>

      {/* Energy Overview */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Daily Consumption</p>
              <p className="text-2xl font-bold text-gray-900">
                {systemStatus.energy?.daily_consumption?.toFixed(2) || '0'} kWh
              </p>
            </div>
            <BoltIcon className="w-8 h-8 text-yellow-500" />
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Cost Saved</p>
              <p className="text-2xl font-bold text-green-600">
                ${systemStatus.energy?.cost_saved?.toFixed(2) || '0.00'}
              </p>
            </div>
            <ChartBarIcon className="w-8 h-8 text-green-500" />
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Active Lights</p>
              <p className="text-2xl font-bold text-blue-600">
                {getRoomStatus().filter(room => room.status === 'on').length}
              </p>
            </div>
            <LightBulbIcon className="w-8 h-8 text-blue-500" />
          </div>
        </motion.div>
      </div>

      {/* Room Status Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
        {getRoomStatus().map((room, index) => (
          <motion.div
            key={room.room}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.1 }}
            className="bg-white p-6 rounded-lg shadow-md"
          >
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900 capitalize">
                {room.room.replace('_', ' ')}
              </h3>
              <div className={`w-3 h-3 rounded-full ${room.motion ? 'bg-green-500' : 'bg-gray-300'}`}></div>
            </div>

            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">Status:</span>
                <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                  room.status === 'on' ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-800'
                }`}>
                  {room.status}
                </span>
              </div>

              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">Brightness:</span>
                <span className="text-sm font-medium">{room.brightness}%</span>
              </div>

              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${room.brightness}%` }}
                ></div>
              </div>

              <div className="flex space-x-2">
                <button
                  onClick={() => controlLight(room.room, 'on')}
                  className="flex-1 bg-blue-600 text-white py-2 px-3 rounded text-sm hover:bg-blue-700"
                >
                  On
                </button>
                <button
                  onClick={() => controlLight(room.room, 'off')}
                  className="flex-1 bg-gray-600 text-white py-2 px-3 rounded text-sm hover:bg-gray-700"
                >
                  Off
                </button>
                <button
                  onClick={() => controlLight(room.room, 'dim', Math.max(0, room.brightness - 20))}
                  className="flex-1 bg-yellow-600 text-white py-2 px-3 rounded text-sm hover:bg-yellow-700"
                >
                  Dim
                </button>
              </div>
            </div>
          </motion.div>
        ))}
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Energy Usage</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={getUsageHistory()}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="time" />
              <YAxis />
              <Tooltip />
              <Line type="monotone" dataKey="consumption" stroke="#3b82f6" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Energy Distribution</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={getEnergyData()}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, percent }) => `${name} ${(percent * 100).toFixed(0)}%`}
                outerRadius={80}
                fill="#8884d8"
                dataKey="value"
              >
                {getEnergyData().map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </motion.div>
      </div>
    </div>
  );
};

export default Dashboard;
