import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { 
  LineChart, Line, AreaChart, Area, BarChart, Bar, 
  XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, 
  PieChart, Pie, Cell, ComposedChart, Legend 
} from 'recharts';
import { 
  BoltIcon, 
  CurrencyDollarIcon, 
  ArrowTrendingUpIcon, 
  ArrowTrendingDownIcon,
  CalendarIcon,
  ClockIcon
} from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';

const EnergyAnalytics = () => {
  const [analytics, setAnalytics] = useState({
    total_consumption: 0,
    daily_consumption: 0,
    monthly_consumption: 0,
    cost_saved: 0,
    usage_history: [],
    savings_percentage: 0
  });
  const [timeRange, setTimeRange] = useState('24h');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAnalytics();
  }, [timeRange]);

  const fetchAnalytics = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/energy/analytics?period=${timeRange}`);
      const data = await response.json();
      setAnalytics(data);
    } catch (error) {
      toast.error('Error fetching energy analytics');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const getChartData = () => {
    const history = analytics.usage_history || [];
    return history.map((entry, index) => ({
      time: index,
      consumption: entry.consumption || 0,
      cost: entry.cost || 0,
      room: entry.room || 'Unknown'
    }));
  };

  const getRoomBreakdown = () => {
    const history = analytics.usage_history || [];
    const roomData = {};

    history.forEach(entry => {
      const room = entry.room || 'Unknown';
      if (!roomData[room]) {
        roomData[room] = { consumption: 0, cost: 0 };
      }
      roomData[room].consumption += entry.consumption || 0;
      roomData[room].cost += entry.cost || 0;
    });

    return Object.entries(roomData).map(([room, data]) => ({
      name: room,
      consumption: data.consumption,
      cost: data.cost,
      color: getRoomColor(room)
    }));
  };

  const getRoomColor = (room) => {
    const colors = {
      'living_room': '#3b82f6',
      'kitchen': '#10b981',
      'bedroom': '#8b5cf6',
      'bathroom': '#f59e0b',
      'office': '#ef4444'
    };
    return colors[room] || '#6b7280';
  };

  const getSavingsTrend = () => {
    const history = analytics.usage_history || [];
    const recent = history.slice(-7);
    const previous = history.slice(-14, -7);

    const recentTotal = recent.reduce((sum, entry) => sum + (entry.consumption || 0), 0);
    const previousTotal = previous.reduce((sum, entry) => sum + (entry.consumption || 0), 0);

    if (previousTotal === 0) return 0;
    return ((previousTotal - recentTotal) / previousTotal) * 100;
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const formatEnergy = (kwh) => {
    return `${kwh.toFixed(2)} kWh`;
  };

  if (loading) {
    return (
      <div className="p-6">
        <div className="animate-pulse">
          <div className="h-8 bg-gray-200 rounded w-1/4 mb-4"></div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
            {[1, 2, 3].map(i => (
              <div key={i} className="h-32 bg-gray-200 rounded"></div>
            ))}
          </div>
          <div className="h-96 bg-gray-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Energy Analytics</h1>
        <p className="text-gray-600 mt-2">
          Detailed energy consumption analysis and cost savings tracking
        </p>

        {/* Time Range Selector */}
        <div className="flex items-center space-x-4 mt-4">
          <span className="text-sm font-medium text-gray-700">Time Range:</span>
          {['24h', '7d', '30d'].map(range => (
            <button
              key={range}
              onClick={() => setTimeRange(range)}
              className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors ${
                timeRange === range
                  ? 'bg-blue-600 text-white'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              {range}
            </button>
          ))}
        </div>
      </div>

      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Total Consumption</p>
              <p className="text-2xl font-bold text-gray-900">
                {formatEnergy(analytics.total_consumption)}
              </p>
            </div>
            <BoltIcon className="w-8 h-8 text-yellow-500" />
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Cost Saved</p>
              <p className="text-2xl font-bold text-green-600">
                {formatCurrency(analytics.cost_saved)}
              </p>
            </div>
            <CurrencyDollarIcon className="w-8 h-8 text-green-500" />
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Savings %</p>
              <p className="text-2xl font-bold text-blue-600">
                {analytics.savings_percentage.toFixed(1)}%
              </p>
            </div>
            <ArrowTrendingDownIcon className="w-8 h-8 text-blue-500" />
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm font-medium text-gray-600">Daily Usage</p>
              <p className="text-2xl font-bold text-purple-600">
                {formatEnergy(analytics.daily_consumption)}
              </p>
            </div>
            <CalendarIcon className="w-8 h-8 text-purple-500" />
          </div>
        </motion.div>
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-8">
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Energy Consumption Over Time</h3>
          <ResponsiveContainer width="100%" height={300}>
            <ComposedChart data={getChartData()}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="time" />
              <YAxis yAxisId="left" />
              <YAxis yAxisId="right" orientation="right" />
              <Tooltip />
              <Legend />
              <Area
                yAxisId="left"
                type="monotone"
                dataKey="consumption"
                stroke="#3b82f6"
                fill="#3b82f6"
                fillOpacity={0.3}
                name="Consumption (kWh)"
              />
              <Line
                yAxisId="right"
                type="monotone"
                dataKey="cost"
                stroke="#ef4444"
                strokeWidth={2}
                name="Cost ($)"
              />
            </ComposedChart>
          </ResponsiveContainer>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Consumption by Room</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={getRoomBreakdown()}
                cx="50%"
                cy="50%"
                labelLine={false}
                label={({ name, consumption }) => `${name}: ${consumption.toFixed(2)} kWh`}
                outerRadius={80}
                fill="#8884d8"
                dataKey="consumption"
              >
                {getRoomBreakdown().map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={entry.color} />
                ))}
              </Pie>
              <Tooltip formatter={(value) => `${value.toFixed(2)} kWh`} />
            </PieChart>
          </ResponsiveContainer>
        </motion.div>
      </div>

      {/* Detailed Analysis */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Cost Breakdown</h3>
          <div className="space-y-4">
            {getRoomBreakdown().map((room, index) => (
              <div key={room.name} className="flex items-center justify-between">
                <div className="flex items-center">
                  <div 
                    className="w-4 h-4 rounded-full mr-3"
                    style={{ backgroundColor: room.color }}
                  ></div>
                  <span className="text-sm font-medium text-gray-700 capitalize">
                    {room.name.replace('_', ' ')}
                  </span>
                </div>
                <div className="text-right">
                  <p className="text-sm font-semibold text-gray-900">
                    {formatCurrency(room.cost)}
                  </p>
                  <p className="text-xs text-gray-500">
                    {formatEnergy(room.consumption)}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.1 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Energy Efficiency Tips</h3>
          <div className="space-y-4">
            <div className="flex items-start space-x-3">
              <div className="w-2 h-2 bg-green-500 rounded-full mt-2"></div>
              <p className="text-sm text-gray-600">
                Use motion sensors to automatically turn off lights when rooms are empty
              </p>
            </div>
            <div className="flex items-start space-x-3">
              <div className="w-2 h-2 bg-blue-500 rounded-full mt-2"></div>
              <p className="text-sm text-gray-600">
                Adjust brightness based on natural light availability
              </p>
            </div>
            <div className="flex items-start space-x-3">
              <div className="w-2 h-2 bg-purple-500 rounded-full mt-2"></div>
              <p className="text-sm text-gray-600">
                Schedule lights to turn off during sleeping hours
              </p>
            </div>
            <div className="flex items-start space-x-3">
              <div className="w-2 h-2 bg-yellow-500 rounded-full mt-2"></div>
              <p className="text-sm text-gray-600">
                Use LED bulbs for better energy efficiency
              </p>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default EnergyAnalytics;
