import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { 
  LightBulbIcon, 
  ChartBarIcon, 
  ClockIcon,
  TrendingUpIcon,
  TrendingDownIcon
} from '@heroicons/react/24/outline';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, BarChart, Bar } from 'recharts';
import toast from 'react-hot-toast';

const AIPredictions = () => {
  const [predictions, setPredictions] = useState({});
  const [optimizations, setOptimizations] = useState([]);
  const [selectedRoom, setSelectedRoom] = useState('living_room');

  useEffect(() => {
    fetchPredictions();
    fetchOptimizations();
  }, [selectedRoom]);

  const fetchPredictions = async () => {
    try {
      const response = await fetch('/api/ai/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          room: selectedRoom,
          time_of_day: getTimeOfDay(),
          day_of_week: getDayOfWeek()
        }),
      });

      if (response.ok) {
        const data = await response.json();
        setPredictions(data);
      }
    } catch (error) {
      console.error('Error fetching predictions:', error);
    }
  };

  const fetchOptimizations = async () => {
    // Simulated optimization data
    setOptimizations([
      {
        type: 'brightness',
        room: selectedRoom,
        current: 80,
        recommended: 60,
        savings: 25,
        reason: 'Natural light available'
      },
      {
        type: 'schedule',
        room: selectedRoom,
        current: 'Always on',
        recommended: 'Motion-based',
        savings: 40,
        reason: 'Low occupancy during day'
      },
      {
        type: 'timing',
        room: selectedRoom,
        current: '6AM-10PM',
        recommended: '7AM-9PM',
        savings: 15,
        reason: 'Optimized for actual usage'
      }
    ]);
  };

  const getTimeOfDay = () => {
    const hour = new Date().getHours();
    if (6 <= hour < 12) return 'morning';
    if (12 <= hour < 17) return 'noon';
    if (17 <= hour < 22) return 'evening';
    return 'night';
  };

  const getDayOfWeek = () => {
    const days = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];
    return days[new Date().getDay()];
  };

  const getPredictionData = () => {
    const hours = Array.from({ length: 24 }, (_, i) => i);
    return hours.map(hour => ({
      hour,
      occupancy: Math.random() > 0.5 ? 1 : 0,
      confidence: Math.random() * 0.4 + 0.6
    }));
  };

  const getOptimizationData = () => {
    return optimizations.map((opt, index) => ({
      name: opt.type,
      current: opt.current,
      recommended: opt.recommended,
      savings: opt.savings
    }));
  };

  const rooms = [
    { id: 'living_room', name: 'Living Room', icon: 'üè†' },
    { id: 'kitchen', name: 'Kitchen', icon: 'üç≥' },
    { id: 'bedroom', name: 'Bedroom', icon: 'üõèÔ∏è' },
    { id: 'bathroom', name: 'Bathroom', icon: 'üöø' },
    { id: 'office', name: 'Office', icon: 'üíº' }
  ];

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">AI Predictions</h1>
        <p className="text-gray-600 mt-2">
          Machine learning insights for optimal energy management
        </p>
      </div>

      {/* Room Selection */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4 mb-8">
        {rooms.map((room) => (
          <motion.button
            key={room.id}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => setSelectedRoom(room.id)}
            className={`p-4 rounded-lg border-2 transition-all ${
              selectedRoom === room.id
                ? 'border-purple-500 bg-purple-50'
                : 'border-gray-200 bg-white hover:border-gray-300'
            }`}
          >
            <div className="text-2xl mb-2">{room.icon}</div>
            <div className="text-sm font-medium text-gray-900">{room.name}</div>
            <div className="text-xs mt-1 text-purple-600">
              AI Active
            </div>
          </motion.button>
        ))}
      </div>

      {/* Current Prediction */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white p-6 rounded-lg shadow-md mb-8"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Current Prediction</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="text-center">
            <div className="text-3xl font-bold text-purple-600 mb-2">
              {predictions.predicted_occupancy ? 'Occupied' : 'Empty'}
            </div>
            <div className="text-sm text-gray-600">Room Status</div>
          </div>

          <div className="text-center">
            <div className="text-3xl font-bold text-blue-600 mb-2">
              {Math.round((predictions.confidence || 0.85) * 100)}%
            </div>
            <div className="text-sm text-gray-600">Confidence</div>
          </div>

          <div className="text-center">
            <div className="text-3xl font-bold text-green-600 mb-2">
              {getTimeOfDay().charAt(0).toUpperCase() + getTimeOfDay().slice(1)}
            </div>
            <div className="text-sm text-gray-600">Time of Day</div>
          </div>
        </div>
      </motion.div>

      {/* 24-Hour Prediction Chart */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white p-6 rounded-lg shadow-md mb-8"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">24-Hour Occupancy Prediction</h2>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={getPredictionData()}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="hour" />
            <YAxis />
            <Tooltip />
            <Line 
              type="monotone" 
              dataKey="occupancy" 
              stroke="#8b5cf6" 
              strokeWidth={3}
              name="Occupancy Probability"
            />
            <Line 
              type="monotone" 
              dataKey="confidence" 
              stroke="#3b82f6" 
              strokeWidth={2}
              strokeDasharray="5 5"
              name="Confidence"
            />
          </LineChart>
        </ResponsiveContainer>
      </motion.div>

      {/* AI Optimizations */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white p-6 rounded-lg shadow-md mb-8"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">AI Optimization Recommendations</h2>
        <div className="space-y-4">
          {optimizations.map((optimization, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: index * 0.1 }}
              className="p-4 border border-gray-200 rounded-lg"
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <LightBulbIcon className="w-6 h-6 text-purple-600 mr-3" />
                  <div>
                    <h3 className="font-medium text-gray-900 capitalize">
                      {optimization.type} Optimization
                    </h3>
                    <p className="text-sm text-gray-600">{optimization.reason}</p>
                  </div>
                </div>
                <div className="text-right">
                  <div className="text-lg font-bold text-green-600">
                    {optimization.savings}% savings
                  </div>
                  <div className="text-sm text-gray-500">
                    {optimization.current} ‚Üí {optimization.recommended}
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </motion.div>

      {/* Learning Insights */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Learning Insights</h3>
          <div className="space-y-4">
            <div className="flex items-center justify-between p-3 bg-blue-50 rounded-lg">
              <div>
                <div className="font-medium text-blue-900">Peak Usage Time</div>
                <div className="text-sm text-blue-700">7:00 PM - 9:00 PM</div>
              </div>
              <TrendingUpIcon className="w-6 h-6 text-blue-600" />
            </div>

            <div className="flex items-center justify-between p-3 bg-green-50 rounded-lg">
              <div>
                <div className="font-medium text-green-900">Low Usage Time</div>
                <div className="text-sm text-green-700">2:00 AM - 6:00 AM</div>
              </div>
              <TrendingDownIcon className="w-6 h-6 text-green-600" />
            </div>

            <div className="flex items-center justify-between p-3 bg-purple-50 rounded-lg">
              <div>
                <div className="font-medium text-purple-900">Pattern Confidence</div>
                <div className="text-sm text-purple-700">87% accuracy</div>
              </div>
              <ChartBarIcon className="w-6 h-6 text-purple-600" />
            </div>
          </div>
        </motion.div>

        <motion.div
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          className="bg-white p-6 rounded-lg shadow-md"
        >
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Energy Impact</h3>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">Current Consumption</span>
              <span className="font-medium">2.4 kWh/day</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">Optimized Consumption</span>
              <span className="font-medium text-green-600">1.6 kWh/day</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">Potential Savings</span>
              <span className="font-medium text-green-600">33%</span>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm text-gray-600">Monthly Cost Savings</span>
              <span className="font-medium text-green-600">$45</span>
            </div>
          </div>

          <div className="mt-6 p-4 bg-yellow-50 rounded-lg">
            <div className="flex items-center mb-2">
              <LightBulbIcon className="w-5 h-5 text-yellow-600 mr-2" />
              <span className="font-medium text-yellow-900">AI Recommendation</span>
            </div>
            <p className="text-sm text-yellow-700">
              Based on your usage patterns, we recommend implementing motion sensors 
              and reducing brightness during low-occupancy hours to maximize savings.
            </p>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default AIPredictions;
