import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { 
  LightBulbIcon, 
  ClockIcon, 
  CalendarIcon,
  CogIcon,
  SunIcon,
  MoonIcon
} from '@heroicons/react/24/outline';
import toast from 'react-hot-toast';

const LightControl = () => {
  const [lights, setLights] = useState({
    living_room: { status: 'off', brightness: 0 },
    kitchen: { status: 'off', brightness: 0 },
    bedroom: { status: 'off', brightness: 0 },
    bathroom: { status: 'off', brightness: 0 },
    office: { status: 'off', brightness: 0 }
  });

  const [schedules, setSchedules] = useState({});
  const [selectedRoom, setSelectedRoom] = useState('living_room');

  useEffect(() => {
    fetchLightStatus();
  }, []);

  const fetchLightStatus = async () => {
    try {
      const response = await fetch('/api/status');
      const data = await response.json();
      setLights(data.lights || {});
    } catch (error) {
      console.error('Error fetching light status:', error);
    }
  };

  const controlLight = async (room, action, brightness = 100) => {
    try {
      const response = await fetch(`/api/lights/${room}/control`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ action, brightness }),
      });

      if (response.ok) {
        const result = await response.json();
        setLights(prev => ({
          ...prev,
          [room]: result.state
        }));
        toast.success(`${action} ${room} lights`);
      }
    } catch (error) {
      toast.error('Error controlling lights');
    }
  };

  const setSchedule = async (room, scheduleData) => {
    try {
      const response = await fetch('/api/schedule', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ room, schedule: scheduleData }),
      });

      if (response.ok) {
        toast.success(`Schedule set for ${room}`);
      }
    } catch (error) {
      toast.error('Error setting schedule');
    }
  };

  const rooms = [
    { id: 'living_room', name: 'Living Room', icon: 'üè†' },
    { id: 'kitchen', name: 'Kitchen', icon: 'üç≥' },
    { id: 'bedroom', name: 'Bedroom', icon: 'üõèÔ∏è' },
    { id: 'bathroom', name: 'Bathroom', icon: 'üöø' },
    { id: 'office', name: 'Office', icon: 'üíº' }
  ];

  return (
    <div className="p-6">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Light Control</h1>
        <p className="text-gray-600 mt-2">
          Manual control and scheduling for all lights
        </p>
      </div>

      {/* Room Selection */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4 mb-8">
        {rooms.map((room) => (
          <motion.button
            key={room.id}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => setSelectedRoom(room.id)}
            className={`p-4 rounded-lg border-2 transition-all ${
              selectedRoom === room.id
                ? 'border-blue-500 bg-blue-50'
                : 'border-gray-200 bg-white hover:border-gray-300'
            }`}
          >
            <div className="text-2xl mb-2">{room.icon}</div>
            <div className="text-sm font-medium text-gray-900">{room.name}</div>
            <div className={`text-xs mt-1 ${
              lights[room.id]?.status === 'on' ? 'text-green-600' : 'text-gray-500'
            }`}>
              {lights[room.id]?.status || 'off'}
            </div>
          </motion.button>
        ))}
      </div>

      {/* Selected Room Control */}
      {selectedRoom && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white p-6 rounded-lg shadow-md mb-8"
        >
          <h2 className="text-xl font-semibold text-gray-900 mb-4">
            {rooms.find(r => r.id === selectedRoom)?.name} Control
          </h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Manual Controls */}
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">Manual Control</h3>
              <div className="space-y-4">
                <div className="flex space-x-2">
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => controlLight(selectedRoom, 'on')}
                    className="flex-1 bg-green-600 text-white py-3 px-4 rounded-lg hover:bg-green-700 flex items-center justify-center"
                  >
                    <SunIcon className="w-5 h-5 mr-2" />
                    Turn On
                  </motion.button>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => controlLight(selectedRoom, 'off')}
                    className="flex-1 bg-gray-600 text-white py-3 px-4 rounded-lg hover:bg-gray-700 flex items-center justify-center"
                  >
                    <MoonIcon className="w-5 h-5 mr-2" />
                    Turn Off
                  </motion.button>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Brightness: {lights[selectedRoom]?.brightness || 0}%
                  </label>
                  <input
                    type="range"
                    min="0"
                    max="100"
                    value={lights[selectedRoom]?.brightness || 0}
                    onChange={(e) => controlLight(selectedRoom, 'dim', parseInt(e.target.value))}
                    className="w-full h-2 bg-gray-200 rounded-lg appearance-none cursor-pointer"
                  />
                </div>
              </div>
            </div>

            {/* Quick Actions */}
            <div>
              <h3 className="text-lg font-medium text-gray-900 mb-4">Quick Actions</h3>
              <div className="space-y-3">
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => controlLight(selectedRoom, 'dim', 30)}
                  className="w-full bg-yellow-600 text-white py-2 px-4 rounded-lg hover:bg-yellow-700 text-sm"
                >
                  Night Mode (30%)
                </motion.button>
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => controlLight(selectedRoom, 'dim', 60)}
                  className="w-full bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 text-sm"
                >
                  Reading Mode (60%)
                </motion.button>
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => controlLight(selectedRoom, 'dim', 100)}
                  className="w-full bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 text-sm"
                >
                  Full Brightness (100%)
                </motion.button>
              </div>
            </div>
          </div>
        </motion.div>
      )}

      {/* All Lights Control */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white p-6 rounded-lg shadow-md mb-8"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">All Lights Control</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => {
              Object.keys(lights).forEach(room => controlLight(room, 'on'));
            }}
            className="bg-green-600 text-white py-3 px-4 rounded-lg hover:bg-green-700 flex items-center justify-center"
          >
            <SunIcon className="w-5 h-5 mr-2" />
            All On
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => {
              Object.keys(lights).forEach(room => controlLight(room, 'off'));
            }}
            className="bg-gray-600 text-white py-3 px-4 rounded-lg hover:bg-gray-700 flex items-center justify-center"
          >
            <MoonIcon className="w-5 h-5 mr-2" />
            All Off
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => {
              Object.keys(lights).forEach(room => controlLight(room, 'dim', 50));
            }}
            className="bg-yellow-600 text-white py-3 px-4 rounded-lg hover:bg-yellow-700 flex items-center justify-center"
          >
            <LightBulbIcon className="w-5 h-5 mr-2" />
            Dim All
          </motion.button>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="bg-purple-600 text-white py-3 px-4 rounded-lg hover:bg-purple-700 flex items-center justify-center"
          >
            <CogIcon className="w-5 h-5 mr-2" />
            AI Mode
          </motion.button>
        </div>
      </motion.div>

      {/* Scheduling */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="bg-white p-6 rounded-lg shadow-md"
      >
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Smart Scheduling</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <h3 className="text-lg font-medium text-gray-900 mb-4">Daily Schedule</h3>
            <div className="space-y-3">
              <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div>
                  <div className="font-medium">Morning (6AM-9AM)</div>
                  <div className="text-sm text-gray-600">Bright lighting for morning routine</div>
                </div>
                <div className="text-green-600 font-medium">ON</div>
              </div>

              <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div>
                  <div className="font-medium">Day (9AM-6PM)</div>
                  <div className="text-sm text-gray-600">Natural light with minimal artificial</div>
                </div>
                <div className="text-yellow-600 font-medium">AUTO</div>
              </div>

              <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div>
                  <div className="font-medium">Evening (6PM-10PM)</div>
                  <div className="text-sm text-gray-600">Comfortable lighting for activities</div>
                </div>
                <div className="text-blue-600 font-medium">ON</div>
              </div>

              <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                <div>
                  <div className="font-medium">Night (10PM-6AM)</div>
                  <div className="text-sm text-gray-600">Minimal lighting for safety</div>
                </div>
                <div className="text-gray-600 font-medium">OFF</div>
              </div>
            </div>
          </div>

          <div>
            <h3 className="text-lg font-medium text-gray-900 mb-4">AI Optimization</h3>
            <div className="space-y-4">
              <div className="p-4 bg-blue-50 rounded-lg">
                <div className="flex items-center mb-2">
                  <CogIcon className="w-5 h-5 text-blue-600 mr-2" />
                  <span className="font-medium text-blue-900">Motion Detection</span>
                </div>
                <p className="text-sm text-blue-700">
                  Lights automatically turn on when motion is detected and off after 5 minutes of inactivity.
                </p>
              </div>

              <div className="p-4 bg-green-50 rounded-lg">
                <div className="flex items-center mb-2">
                  <SunIcon className="w-5 h-5 text-green-600 mr-2" />
                  <span className="font-medium text-green-900">Natural Light Integration</span>
                </div>
                <p className="text-sm text-green-700">
                  Brightness automatically adjusts based on available natural light.
                </p>
              </div>

              <div className="p-4 bg-purple-50 rounded-lg">
                <div className="flex items-center mb-2">
                  <CalendarIcon className="w-5 h-5 text-purple-600 mr-2" />
                  <span className="font-medium text-purple-900">Usage Learning</span>
                </div>
                <p className="text-sm text-purple-700">
                  AI learns your patterns and optimizes lighting schedules accordingly.
                </p>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  );
};

export default LightControl;
